<js>
let un=[];
let server="https://dyzb77.jsonserver.workers.dev/posts";
let url="https://cdn.jsdelivr.net/gh/orzend/yuedu/dybz77.json";
let obj=JSON.parse(java.ajax(url));
result=result.replace(/<img src="(.+?)">/g,(html,text)=>{
code='#'+text.substring(12,22)+'#'
text=obj[code]
if(!text){
text=code;
//java.log("未收录"+code);
un.push(code);
}
return text
})
if(un.length!=0){
body=un;
//java.log(un)
post={
    method:"POST",
    body:String(body),
    };
java.ajax(server+"," + JSON.stringify(post))}
</js>

